import { useState } from "react";

const useHttp = async (requestConfig, getData) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const sendRequest = async () => {
    setIsLoading(true);
    setError(null);

   /*  try {
      const response = await fetch(requestConfig.url, {
        method: requestConfig.method ? requestConfig.method : 'GET',
        headers: requestConfig.headers ? requestConfig.headers : {},
        body: requestConfig.body ? JSON.stringify(requestConfig.body) : null,
        
      }); */
        try {
      const response = await fetch(
        "https://react-http-cbdbb-default-rtdb.europe-west1.firebasedatabase.app/tasks.json"
      ); 

      if (!response.ok) {
        throw new Error("Request failed!");
      }

      const data = await response.json();
      console.log(data, requestConfig, getData, response);
      getData(data);
    } catch (err) {
      setError(err.message || "Something went wrong!");
    }
    setIsLoading(false);

    console.log(error, isLoading, sendRequest);
  };
  return {
    error,
    isLoading,
    sendRequest,
  };
};
export default useHttp;
