import { useState } from "react";

const useHttp = async (requestConfig, getData) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
   
    const sendRequest = async () => {
      setIsLoading(true);
      setError(null);
     let response = {}
    
       try {

            /*  response = await fetch(requestConfig.url ,
                {
                  method: requestConfig.method ? requestConfig.method :'GET',
                  body: requestConfig.body ? JSON.stringify(requestConfig.body)  : null,
                  headers: requestConfig.headers? requestConfig.headers : {}
                }) */
                response = await fetch('https://react-http-cbdbb-default-rtdb.europe-west1.firebasedatabase.app/tasks.json',
                    {  method: 'GET'
                     
                    })
                                
          
      if (!response.ok) {
        throw new Error("Request failed!");
      }

      const data = await response.json();
     getData(data)
     
    }  catch (err) {
        setError(err.message || 'Something went wrong!');
      }
      setIsLoading(false);
    
      console.log(error,isLoading ,sendRequest)
    }
    return {
        error,
        isLoading,
        sendRequest
    }
}
export default useHttp;
